<?php defined('SYSPATH') OR die('No direct access allowed.');class Controller_Backend_Page extends Controller_Backend_BaseAdmin {	protected $_module_name;	protected $_class_name;	protected $_search_keys;	protected $_prefs;	protected $_upload_path;	protected $page;	protected $category;	protected $categories;	protected $file;	protected $statuses;		public function before () {		parent::before();								$category				= new Model_PageCategory;		$file					= new Model_PageFile;		$categories				= $category->find();		$buffers				= array();				$this->_module_name		= Request::$current->controller();		$this->_class_name		= strtolower(str_replace('Controller_Backend_', '', get_class($this)));		$this->_module_menu		= $this->acl->module_menu;		$this->_search_keys		= array('title'			=> 'Title',										'category_id'	=> 'Category',										'status'		=> 'Status');		$this->_prefilter_keys	= array('category_id');		$this->_prefs			= (Lib::config($this->_module_name.'.'.$this->_class_name.'_fields') !== NULL) ? Lib::config($this->_module_name.'.'.$this->_class_name.'_fields') : array();		$this->_upload_path		= (Lib::config($this->_module_name.'.upload_path') !== NULL) ? Lib::config($this->_module_name.'.upload_path') : array();		$this->_upload_url		= (Lib::config($this->_module_name.'.upload_url') !== NULL) ? Lib::config($this->_module_name.'.upload_url') : array();				$this->page				= new Model_Page;		$this->pagecontent		= new Model_PageContent;				$this->category			= new Model_PageCategory;		$this->categorycontent	= new Model_PageCategoryContent;				$this->file				= new Model_PageFile;		$this->user				= new Model_User;		foreach ($categories as $row) {			$buffers[$row->id]	= $row;		}		$this->categories		= $buffers;		$users					= $this->user->find();		$buffers				= array();		foreach ($users as $user){			$buffers[$user->id] = $user;		}		$this->_users			= $buffers;				unset($buffers);		//-- User id from user login session 'user_id'		$this->_uid				= $this->session->get('user_id');		//-- Default news statuses		$this->statuses			= array('publish',										'unpublish');		unset($category, $categories, $buffers);	}		public function action_index () {		$category_id		= !empty($this->id1) ? $this->id1 : '';		$this->category->id	= $category_id;		if ($category_id != '' && !$this->category->load())			$category_id	= '';		$where_cond	= array('status !='	=> 'deleted');		if ($category_id != '')			$where_cond['category_id']	= $category_id;		/** Find & Multiple change status **/		if ($_POST) {			$post	= new Validation($_POST);			if (isset($post['field']) || isset($post['keyword'])) {				$post->rule('keyword', 'regex', array(':value', '/^[a-z_.\-]++$/iD'));				if ($post->check()) {					if (!in_array($post['field'], $this->_prefilter_keys))						$where_cond[$post['field'] . ' LIKE']	= $post['keyword'].'%';					else						$where_cond[$post['field'] . ' IN']	= call_user_func(array($this, '_'.$post['field']), $post['keyword']);					$filters	= array('f'	=> $post['field'],										'q'	=> $post['keyword']);					$this->session->set($this->_class_name.'_filter', serialize($filters));				} else if (isset($post['find'])) {					$this->session->delete($this->_class_name.'_filter');				}			}			if ($this->session->get($this->_class_name.'_filter') !== FALSE) {				$filters	= unserialize($this->session->get($this->_class_name.'_filter'));				if (in_array($filters['f'], array_keys($this->_search_keys)) && $filters['q'] != '') {					if (!in_array($filters['f'], $this->_prefilter_keys))						$where_cond[$filters['f'] . ' LIKE']	= $filters['q'].'%';					else						$where_cond[$filters['f'] . ' IN']	= call_user_func(array($this, '_'.$filters['f']), $filters['q']);				}			}		}		/** Table sorting **/		$params		= Request::$current->param();				$sorts		= array('asc', 'desc');				$sort		= isset($params['id2']) ? $this->id2 : 'title';		$order		= isset($params['id4']) ? $this->id4 : $sorts[0];		$order_by	= array($sort => $order);		$page_index	= isset($_GET['page']) ? $_GET['page'] - 1: 0;		$per_page	= Lib::config('admin.item_per_page');			//$per_page	= 10;		$page_url	= isset($_GET['page']) ? '?page='.$_GET['page'] : '';		$offset		= ($page_index == 0) ? '' : $page_index * $per_page;		$table_headers	= array('title'			=> 'Title',								'category_id'	=> 'Category',								/*'count'			=> 'Views',*/								'status'		=> 'Status',								'added'			=> 'Added',								'modified'		=> 'Modified');		if (isset($params['id2']) && isset($params['id4'])) {			$headers	= array_keys($table_headers);			$sort		= (isset($params['id2']) && in_array(strtolower($params['id2']), $headers)) ? strtolower($params['id2']) : $headers[0];			$order		= (isset($params['id4']) && in_array(strtolower($params['id4']), $sorts)) ? strtolower($params['id4']) : $sorts[0];			$order_by	= array($sort=> $order);		}		if (isset($params['id3']) && isset($params['id5'])) {			$headers	= array_keys($table_headers);			$sort		= (isset($params['id3']) && in_array(strtolower($params['id3']), $headers)) ? strtolower($params['id3']) : $headers[0];			$order		= (isset($params['id5']) && in_array(strtolower($params['id5']), $sorts)) ? strtolower($params['id5']) : $sorts[0];			$order_by	= array($sort=> $order);		}		/** Execute list query **/		$field		= isset($filters['f']) ? $filters['f'] : '';		$keyword	= isset($filters['q']) ? $filters['q'] : '';		$where_cond	= isset($where_cond) ? $where_cond : '';		$total_rows	= count($this->page->find($where_cond));		$total_record 	= $total_rows;		$listings	= $this->page->find($where_cond, $order_by, $per_page, $offset);				// List Page Detail collection		$detail_data = $this->pagecontent->find(array('lang_id' => Lang::_default()));		$buffers = array();		foreach($detail_data as $data1){			$buffers[$data1->page_id] = $data1;		}		$detail_data = $buffers;				// List Category Detail collection		$category_data = $this->categorycontent->find(array('lang_id' => Lang::_default()));		$buffers = array();		foreach($category_data as $data2){			$buffers[$data2->category_id] = $data2;		}		$category_data = $buffers;				$pagination	= Pagination::factory(array(			'total_items' 		=> $total_rows,			'items_per_page' 	=> $per_page,		));				/** Views **/		$content_vars		= array(									'detail_data'	=> $detail_data,									'listings'		=> $listings,									'table_headers'	=> $table_headers,									'statuses'		=> $this->statuses,									'search_keys'	=> $this->_search_keys,									'module_name'	=> $this->_module_name,									'class_name'	=> $this->_class_name,									'module_menu'	=> $this->_module_menu,									'total_record'	=> $total_record,												'field'			=> $field,									'keyword'		=> $keyword,									'order'			=> $order,									'sort'			=> $sort,									'page_url'		=> $page_url,									'page_index'	=> $offset,									'params'		=> $params,									'pagination'	=> $pagination,									'categories'	=> $this->categories,									'category_data'	=> $category_data,									'category_id'	=> $category_id);		$content			= View::factory($this->_module_name.'/backend/'.$this->_module_name.'_index');		foreach ($content_vars as $var => $val) {			$content->$var	= $val;		}		$this->template->content		= $content; 	}		public function action_add () {		$category_id = $this->id1;		$fields	= array('category_id'		=> '',						'title'				=> '',												'attribute'			=> '',						'publish_date'		=> '',						'unpublish_date'	=> '',						'show_cover'		=> '',												'show_image'		=> '',																		'allow_comment'		=> '',						'tags'				=> '',						'order'				=> '',						'status'			=> '');		if (isset($this->_prefs['show_upload']) && $this->_prefs['show_upload'] && isset($this->_prefs['uploads'])) {			foreach ($this->_prefs['uploads'] as $row_name => $row_params) {				$fields[$row_name]	= '';				if (isset($row_params['caption']) && $row_params['caption'])					$fields[$row_name.'_caption']	= '';			}		}		$errors	= $fields;		$fields['category_id']	= $category_id;		if ($_POST) {			if ($_FILES)				$post	= new Validation(array_merge($_POST, $_FILES));			else				$post	= new Validation($_POST);			$post->rule('title', 'not_empty');								$post->rule('title', array($this, '_safe_html_title'), array(':validation', ':field', 'title'));			$post->rule('order', 'numeric');			$post->rule('show_cover', 'numeric');						$post->rule('show_image', 'numeric');			$post->rule('title', array($this, '_unique_title'), array(':validation', ':field', 'title'));			if (isset($this->_prefs['show_upload']) && $this->_prefs['show_upload'] && isset($this->_prefs['uploads'])) {				foreach ($this->_prefs['uploads'] as $row_name => $row_params) {					//if (isset($row_params['optional']) && !$row_params['optional']) {						/*$post->add_rules($row_name, 'upload::required');*/						//$post->add_rules($row_name, 'upload::valid');					//}					//if (isset($row_params['file_type']))						//$post->add_rules($row_name, 'upload::type['.$row_params['file_type'].']');					//if (isset($row_params['max_file_size']))						//$post->add_rules($row_name, 'upload::size['.$row_params['max_file_size'].']');				}			}						if ($post->check()) {				$fields	= $post->as_array();				$where_cond	= array('category_id'		=> $fields['category_id']);				$last_order	= $this->page->find_count($where_cond) + 1;				if (isset($fields['order']) && $fields['order'] < $last_order) {					$where_cond	= array('category_id'	=> $fields['category_id'],										'order >='		=> $fields['order']);					$pages	= $this->page->find($where_cond);					foreach ($pages as $row) {						$row->order	= $row->order + 1;						$row->update();					}					unset($pages);				}				$params	= array('category_id'		=> $fields['category_id'],								'title'				=> $fields['title'],								'attribute'			=> isset($fields['attribute']) ? $fields['attribute'] : '',								'publish_date'		=> isset($fields['publish_date']) ? $this->_reverse_date($fields['publish_date']) : '0000-00-00',								'unpublish_date'	=> isset($fields['unpublish_date']) ? $this->_reverse_date($fields['unpublish_date']) : '0000-00-00',								'show_cover'		=> isset($fields['show_cover']) ? $this->_reverse_date($fields['show_cover']) : '',								'show_image'		=> isset($fields['show_image']) ? $this->_reverse_date($fields['show_image']) : '',								   'allow_comment'	   => isset($fields['allow_comment']) ? $fields['allow_comment'] : 0,								'tags'				=> isset($fields['tags']) ? $fields['tags'] : '',								'order'				=> isset($fields['order']) ? $fields['order'] : 0,					'user_id'		=> (isset($this->acl->user->id)) ? $this->acl->user->id : 0,								'status'			=> $fields['status']);				$id		= $this->page->add($params);				$params2 = array();								foreach($post['detail'] as $detail) {										$params2[]	= array(										'page_id'	=> !empty($id) ? $id : 0,										'lang_id'	=> $detail['lang_id'],										'subject'	=> $detail['subject'],										'synopsis'	=> $detail['synopsis'],										'text'		=> $detail['text'],								);				}								foreach ($params2 as $row => $val) {					$this->pagecontent->add($val);				}				if ($id !== FALSE && isset($this->_prefs['show_upload']) && $this->_prefs['show_upload'] && isset($this->_prefs['uploads'])) {					foreach ($this->_prefs['uploads'] as $row_name => $row_params) {						//if (!upload::required($_FILES[$row_name]) || !upload::valid($_FILES[$row_name]))							//continue;						if (!Upload::not_empty($_FILES[$row_name]) || !Upload::type($_FILES[$row_name],explode(',',$row_params['file_type'])) || !Upload::valid($_FILES[$row_name]))							continue;						if (!File::exts_by_mime($post[$row_name]['type']))							continue;						$file_hash	= md5(time() + rand(100, 999));						$file_data	= pathinfo($_FILES[$row_name]['name']);						$file_name	= Lib::_upload_to($post[$row_name], $file_hash.'.'.$file_data['extension'], $this->_upload_path, 0755);						$file_data	= pathinfo($file_name);						$file_mime	= $_FILES[$row_name]['type'];						if ($file_name != '' && isset($this->_prefs['uploads'][$row_name]['image_manipulation'])) {							$params			= array('page_id'	=> $id,													'field_name'	=> $row_name,													'file_name'		=> $file_data['basename'],													'file_type'		=> $file_mime,													'caption'		=> isset($fields[$row_name.'_caption']) ? $fields[$row_name.'_caption'] : '');							$this->file->add($params);						}					}				}				if (isset($_POST['add_another'])) {					$this->request->redirect(ADMIN.$this->_class_name.'/add');					return;				}				$this->request->redirect(ADMIN.$this->_class_name.'/view/'.$id);				return;			} else {				$fields		= Arr::overwrite($fields, $post->as_array());				$errors 	= Arr::overwrite($errors, $post->errors('validation'));				$buffers	= $errors;				foreach ($errors as $row_key => $row_val) {					if ($row_val != '') {						$buffers[$row_key]	= Lib::config('admin.error_field_open').ucfirst($row_val).Lib::config('admin.error_field_close');					} else {						$buffers[$row_key]	= $row_val;					}				}				$errors		= $buffers;			}		}		// List Category Detail collection		$category_data = $this->categorycontent->find(array('lang_id' => Lang::_default()));		$buffers = array();		foreach($category_data as $data2){			$buffers[$data2->category_id] = $data2;		}		$category_data = $buffers;		/** Generate Thumbnails **/		Lib::_auto_image_manipulation($this->_upload_path, $this->file, $this->_prefs);		/** Views **/		$where_cond			= array('status !='		=> 'deleted');		$order_by			= array('category_id'	=> 'ASC',									'order'			=> 'ASC');		$orders				= $this->page->find($where_cond, $order_by);		$content_vars		= array('language_data' => $this->language_data,									'category_data' => $category_data,									'errors'		=> $errors,									'fields'		=> $fields,									'categories'	=> $this->categories,									'orders'		=> $orders,									'statuses'		=> $this->statuses,									'class_name' 	=> $this->_class_name,										'module_name' 	=> $this->_module_name,									'module_menu'	=> $this->_module_menu);		$content_vars		= array_merge($content_vars, $this->_prefs);		$content			= View::factory($this->_module_name.'/backend/'.$this->_module_name.'_add');		foreach ($content_vars as $var => $val) {			$content->$var	= $val;		}		$this->template->content		= $content; 	}		public function action_view () {		$id = $this->id1;		$category_id = $this->id2;		$this->page->id	= $id;		if (!$this->page->load()) {			$this->request->redirect(ADMIN.$this->_class_name.'/error/invalid_request');			return;		}		/** Views **/		if ($this->page->category_id != 0) {			$category		= $this->category->load($this->page->category_id);			$category		= HTML::chars($category->title, TRUE);		} else {			$category		= 'This page doesn\'t have category';		}		if ($this->page->order == 0) {			$order			= '-';		} else if ($this->page->order != 1) {			$where_cond		= array('category_id'	=> $this->page->category_id,									'order'			=> ($this->page->order - 1));			$pages		= $this->page->find($where_cond, '', 1);			if (isset($pages[0]))				$order		= 'After category '.HTML::chars($pages[0]->subject, TRUE);			else				$order		= '';		} else {			$order			= 'At the beginning';		}		$where_cond			= array('page_id'	=> $this->page->id);		$files				= $this->file->find($where_cond);		$buffers			= array();		foreach ($files as $row) {			$buffers[$row->field_name]	= $row;		}		$files				= $buffers;		unset($buffers);										// Page detail for language based on content id within language ids		$detail_data	= $this->pagecontent->find(array('id !='=>'0','page_id' => $this->page->id,'lang_id IN'=>Lang::_get_lang()));		$buffers = array();		foreach($detail_data as $data){			$buffers[$data->lang_id] = $data;		}		$detail_data = $buffers;					/** Generate Thumbnails **/		Lib::_auto_image_manipulation($this->_upload_path, $this->file, $this->_prefs);		/** Views **/		$content_vars		= array(									'language_data' => $this->language_data,									'detail_data'	=> $detail_data,									'page'			=> $this->page,									'category'		=> $category,									'files'			=> $files,									'order'			=> $order,									'readable_mime'	=> Lib::config($this->_module_name.'.readable_mime'),									'category_id'	=> $category_id,									'module_name' => $this->_module_name,									'module_menu'	=> $this->_module_menu,);		$content_vars		= array_merge($content_vars, $this->_prefs);		$content			= View::factory($this->_module_name.'/backend/'.$this->_module_name.'_view');		foreach ($content_vars as $var => $val) {			$content->$var	= $val;		}		$this->template->content		= $content; 	}		public function action_edit () {		$id = $this->id1;		$category_id = $this->id2;		$this->page->id	= $id;		if (!$this->page->load()) {			$this->request->redirect(ADMIN.$this->_class_name.'/error/invalid_request');			return;		}		$fields	= array('category_id'		=> '',						'title'				=> '',						'attribute'			=> '',						'publish_date'		=> '',						'unpublish_date'	=> '',						'show_cover'		=> '',												'show_image'		=> '',												'allow_comment'		=> '',						'tags'				=> '',						'order'				=> '',						'status'			=> '');		if (isset($this->_prefs['show_upload']) && $this->_prefs['show_upload'] && isset($this->_prefs['uploads'])) {			foreach ($this->_prefs['uploads'] as $row_name => $row_params) {				$fields[$row_name]	= '';				if (isset($row_params['caption']) && $row_params['caption'])					$fields[$row_name.'_caption']	= '';			}		}		$errors	= $fields;		if ($_POST) {			if ($_FILES)				$post	= new Validation(array_merge($_POST, $_FILES));			else				$post	= new Validation($_POST);						$post->rule('title', 'not_empty');			$post->rule('subject', 'min_length', array(':value', 4));			$post->rule('order', 'numeric');						if (isset($this->_prefs['show_upload']) && $this->_prefs['show_upload'] && isset($this->_prefs['uploads'])) {				foreach ($this->_prefs['uploads'] as $row_name => $row_params) {					// if (isset($row_params['file_type']))						// $post->add_rules($row_name, 'upload::type['.$row_params['file_type'].']');					// if (isset($row_params['max_file_size']))						// $post->add_rules($row_name, 'upload::size['.$row_params['max_file_size'].']');					if (!File::exts_by_mime($post[$row_name]['type']))						continue;					}			}			// $post->add_callbacks('name', array($this, '_unique_name'));//			/print_r($fields['category_id']); exit(			//print_r($_POST); exit();			$fields['category_id'] = (empty($fields['category_id'])) ? '0' : $fields['category_id'];			if ($post->check()) {				$fields	= $post->as_array();				$order		= (@$fields['order'] == 1) ? @$fields['order'] : (@$fields['order'] - 1);				$where_cond	= array('category_id'	=> $fields['category_id']);				$last_order	= $this->page->find_count($where_cond) + 1;				if ($this->page->category_id != $fields['category_id']) {					$where_cond	= array('category_id'	=> $this->page->category_id,										'order >='		=> $this->page->order);					$orders		= $this->page->find($where_cond);					foreach ($orders as $row) {						$row->order	= $row->order - 1;						$row->update();					}					$where_cond	= array('category_id'	=> $fields['category_id'],										'order >='	=> $order);					$orders		= $this->page->find($where_cond);					foreach ($orders as $row) {						$row->order	= $row->order + 1;						$row->update();					}				} else {					if (@$fields['order'] == 1) {						$where_cond	= array('category_id'	=> $fields['category_id'],											'order >='		=> $order,											'order <='		=> $this->page->order,											'status !='		=> 'deleted');						$orders		= $this->page->find($where_cond);						foreach ($orders as $row) {							$row->order	= $row->order + 1;							$row->update();						}					} else if ($this->page->order < @$fields['order']) {						$where_cond	= array('category_id'	=> $fields['category_id'],											'order >'		=> $this->page->order,											'order <='		=> $order,											'status !='		=> 'deleted');						$orders		= $this->page->find($where_cond);						foreach ($orders as $row) {							$row->order	= $row->order - 1;							$row->update();						}					} else if ($this->page->order > @$fields['order']) {						$where_cond	= array('category_id'	=> $fields['category_id'],											'order >'		=> $order,											'order <='		=> $this->page->order,											'status !='		=> 'deleted');						$orders		= $this->page->find($where_cond);						foreach ($orders as $row) {							$row->order	= $row->order + 1;							$row->update();						}						$order		= $order + 1;					} else if ($this->page->order == @$fields['order']) {						$order		= $order + 1;					}				}				unset($orders);				$params	= array('category_id'		=> $fields['category_id'],								'title'				=> $fields['title'],								'attribute'			=> isset($fields['attribute']) ? $fields['attribute'] : '',								'publish_date'		=> isset($fields['publish_date']) ? $this->_reverse_date($fields['publish_date']) : '0000-00-00',								'unpublish_date'	=> isset($fields['unpublish_date']) ? $this->_reverse_date($fields['unpublish_date']) : '0000-00-00',								'allow_comment'		=> isset($fields['allow_comment']) ? $fields['allow_comment'] : 0,								'show_cover'		=> isset($fields['show_cover']) ? $fields['show_cover'] : 0,								'show_image'		=> isset($fields['show_image']) ? $fields['show_image'] : 0,								'tags'				=> isset($fields['tags']) ? $fields['tags'] : '',								'order'				=> isset($fields['order']) ? $order : 0,								'status'			=> $fields['status']);				foreach ($params as $var => $val) {					$this->page->$var	= $val;				}				$this->page->update();								$params2 = array();				foreach($fields['detail'] as $detail) {										$params2[$detail['lang_id']]	= array(										'id'			=> !empty($detail['id']) ? $detail['id'] : '',										'page_id'		=> $this->page->id,																'lang_id'		=> $detail['lang_id'],										'subject'		=> $detail['subject'],										'synopsis'		=> $detail['synopsis'],																				'text'			=> $detail['text']									);				}				foreach ($params2 as $row => $val) {					if (!empty($row) && !empty($val['subject']) && !empty($val['id'])) {						$this->pagecontent->id			= $val['id'];						$this->pagecontent->page_id		= $val['page_id'];						$this->pagecontent->lang_id		= $val['lang_id'];						$this->pagecontent->subject		= $val['subject'];						$this->pagecontent->synopsis	= $val['synopsis'];												$this->pagecontent->text		= $val['text'];						$this->pagecontent->update();					} else {						$this->pagecontent->add($val);					}				}									if (isset($this->_prefs['show_upload']) && $this->_prefs['show_upload'] && isset($this->_prefs['uploads'])) {					$where_cond			= array('page_id'	=> $this->page->id);					$files				= $this->file->find($where_cond);					$buffers			= array();					foreach ($files as $row) {						$buffers[$row->field_name]	= $row;					}					$files				= $buffers;					unset($buffers);					foreach ($this->_prefs['uploads'] as $row_name => $row_params) {						if (isset($fields['delete_'.$row_name]) && $fields['delete_'.$row_name] == 1 && isset($files[$row_name])) {							$this->file->id	= $files[$row_name]->id;							$this->file->load();							$this->file->delete();						}						// if (!isset($_FILES[$row_name]) || (isset($_FILES[$row_name]) && !upload::required($_FILES[$row_name]) || !upload::valid($_FILES[$row_name])))							// continue;						if (!Upload::not_empty($post[$row_name]) || !Upload::type($post[$row_name],explode(',',$row_params['file_type'])) || !Upload::valid($post[$row_name]))							continue;						$file_hash	= md5(time() + rand(100, 999));						$file_data	= pathinfo($post[$row_name]['name']);						$file_name	= Lib::_upload_to($post[$row_name], $file_hash.'.'.$file_data['extension'], $this->_upload_path, 0755);						$file_data	= pathinfo($file_name);						$file_mime	= $post[$row_name]['type'];						if (!isset($files[$row_name])) {							$params			= array('page_id'	=> $id,													'field_name'	=> $row_name,													'file_name'		=> $file_data['basename'],													'file_type'		=> $file_mime,													'caption'		=> isset($fields[$row_name.'_caption']) ? $fields[$row_name.'_caption'] : '');							$this->file->add($params);						} else {							$this->file->id	= $files[$row_name]->id;							$this->file->load();							$params			= array('page_id'	=> $id,													'field_name'	=> $row_name,													'file_name'		=> $file_data['basename'],													'file_type'		=> $file_mime,													'caption'		=> isset($fields[$row_name.'_caption']) ? $fields[$row_name.'_caption'] : '');							foreach ($params as $var => $val) {								$this->file->$var	= $val;							}							$this->file->update();						}					}				}				if ($category_id != '')					$this->request->redirect(ADMIN.$this->_class_name.'/view/'.$this->page->id.'/'.$category_id);				else					$this->request->redirect(ADMIN.$this->_class_name.'/view/'.$this->page->id);				return;			} else {				$fields		= Arr::overwrite($fields, $post->as_array());				$errors 	= Arr::overwrite($errors, $post->errors('validation'));				$buffers	= $errors;				foreach ($errors as $row_key => $row_val) {					if ($row_val != '') {						$buffers[$row_key]	= Lib::config('admin.error_field_open').ucfirst($row_val).Lib::config('admin.error_field_close');					} else {						$buffers[$row_key]	= $row_val;					}				}				$errors		= $buffers;			}		} else {			$fields	= array('category_id'		=> $this->page->category_id,							'title'				=> $this->page->title,							'attribute'			=> $this->page->attribute,							'publish_date'		=> ($this->page->publish_date != '0000-00-00') ? $this->_reverse_date($this->page->publish_date) : '',							'unpublish_date'	=> ($this->page->unpublish_date != '0000-00-00') ? $this->_reverse_date($this->page->unpublish_date) : '',							'allow_comment'		=> $this->page->allow_comment,							'show_cover'		=> $this->page->show_cover,							'show_image'		=> $this->page->show_image,														'tags'				=> $this->page->tags,							'order'				=> $this->page->order,							'status'			=> $this->page->status);			$where_cond			= array('page_id'	=> $this->page->id);			$files				= $this->file->find($where_cond);			$buffers			= array();			foreach ($files as $row) {				$buffers[$row->field_name]	= $row;			}			$files				= $buffers;			unset($buffers);			if (isset($this->_prefs['show_upload']) && $this->_prefs['show_upload'] && isset($this->_prefs['uploads'])) {				foreach ($this->_prefs['uploads'] as $row_name => $row_params) {					$fields[$row_name]	= '';					if (isset($row_params['caption']) && $row_params['caption'])						$fields[$row_name.'_caption']	= (isset($files[$row_name])) ? $files[$row_name]->caption : '';				}			}		}						// Page detail for language based on content id within language ids		$detail_data	= $this->pagecontent->find(array('id !='=>'0','page_id' => $this->page->id,'lang_id IN'=>Lang::_get_lang()));		$buffers = array();		foreach($detail_data as $data){			$buffers[$data->lang_id] = $data;		}		$detail_data = $buffers;						/** Views **/		$where_cond			= array('status !='		=> 'deleted');		$order_by			= array('category_id'	=> 'ASC',									'order'			=> 'ASC');		$orders				= $this->page->find($where_cond, $order_by);		$content_vars		= array('language_data' => $this->language_data,									'detail_data'	=> $detail_data,									'errors'		=> $errors,									'fields'		=> $fields,									'class_name'	=> $this->_class_name,																		'module_name'	=> $this->_module_name,									'module_menu'	=> $this->_module_menu,									'page'			=> $this->page,									'categories'	=> $this->categories,									'files'			=> $files,									'orders'		=> $orders,									'statuses'		=> $this->statuses,									'readable_mime'	=> Lib::config($this->_module_name.'.readable_mime'),									'category_id'	=> $category_id);		$content_vars		= array_merge($content_vars, $this->_prefs);		$content			= View::factory($this->_module_name.'/backend/'.$this->_module_name.'_edit');		foreach ($content_vars as $var => $val) {			$content->$var	= $val;		}		$this->template->content		= $content;	}		public function action_delete () {		$this->page->id	= $this->id1;		if (!$this->page->load()) {			$this->request->redirect(ADMIN.$this->_class_name.'/error/invalid_request');			return;		}		$this->page->status	= 'deleted';		if($this->page->update())			echo 1;		else			echo 0;		exit;//		$this->session->set_flash('function_delete', 'success');////		$redirect_url	= ($this->acl->previous_url != '') ? $this->acl->previous_url : ADMIN.$this->_class_name.'/index';////		$this->request->redirect($redirect_url);	}		public function action_download() {		$files		= $this->id1;		$where_cond	= array('file_name'	=> $files);		$files		= $this->file->find($where_cond);		foreach ($files as $row) {			Lib::_download(Lib::config($this->_module_name.'.upload_url').$row->file_name);		}	}			/*** Function Access ***/	// Action for update item status	public function action_change() {		if ($this->request->post() && $this->request->post('check') !='') {			$rows	= $this->request->post('check');			foreach ($rows as $row) {				$this->page->id	= $row;				if (!$this->page->load())					continue;				$this->page->status	= $this->request->post('select_action');				$this->page->update();			}			$redirect_url	= (strstr($this->acl->previous_url,ADMIN)) ? $this->acl->previous_url : ADMIN.$this->_class_name.'/index';			$this->request->redirect($redirect_url);		} else {			$this->request->redirect(ADMIN.$this->_class_name);		}	}		/** CALLBACKS **/	public function _valid_category_id (Validation $array, $field) {		if ($array[$field] == 0)			return TRUE;		$where_cond		= array('id'	=> $array[$field]);		$parent_exists	= ($this->category->find_count($where_cond) != 0);		if (!$parent_exists)			$array->error($field, 'invalid_category_id');	}		public function _unique_title (Validation $array, $field) {		if (isset($this->page->title) && $this->page->title == $array[$field])			return;		$where_cond		= array('title'	=> $array[$field]);		$name_exists	= ($this->page->find_count($where_cond) != 0);		if ($name_exists)			$array->error($field, 'title_exists');	}		public function _valid_search_key ($value = '') {		return mysql_real_escape_string($value);	}		/** PRE FILTER **/	public function _safe_html_title ($value = '') {		//return htmlentities($value);		return $value;	}		public function _reverse_date ($value = '') {		if (strpos($value, '/') != 0)			return (implode('-', array_reverse(explode('/', $value))));		else			return (implode('/', array_reverse(explode('-', $value))));	}		public function _category_id ($value = '') {		$where_cond	= array('title LIKE'	=> '%'.$value.'%',							'status !='	=> 'deleted');		$buffers	= $this->category->find($where_cond);		$ids		= array();		foreach ($buffers as $row) {			$ids[]	= $row->id;		}		return $ids;	}}